// COPY TO CLIPBOARD
// Attempts to use .execCommand('copy') on a created text field
// Falls back to a selectable alert if not supported
// Attempts to display status in Bootstrap tooltip
// ------------------------------------------------------------------------------

function copyToClipboard(text, el) {
  let copyTest = document.queryCommandSupported('copy');
  let elOriginalText = el.attr('data-original-title');

  if (copyTest === true) {
    let copyTextArea = document.createElement("textarea");
    copyTextArea.value = text;
    document.body.appendChild(copyTextArea);
    copyTextArea.select();
    try {
      let successful = document.execCommand('copy');
      let msg = successful ? 'Copied!' : 'Whoops, not copied!';
      el.attr('data-original-title', msg).tooltip('show');
    } catch (err) {
      console.log('Oops, unable to copy');
    }
    document.body.removeChild(copyTextArea);
    el.attr('data-original-title', elOriginalText);
  } else {
    // Fallback if browser doesn't support .execCommand('copy')
    window.prompt("Copy to clipboard: Ctrl+C or Command+C, Enter", text);
  }
}

$(document).ready(function () {

  $(".expandDetails").on("click", function (event) {
    event.stopPropagation();
    $(this).siblings(".info-long").slideToggle(500);
    // $(this).children(".arrowDown").toggle();
    // $(this).children(".arrowUp").toggle();

    $(this).children('.arrowDown').toggleClass('hidden');

    if ($(this).children('.arrowUp').is('.hidden')) {

      $(this).children('.arrowUp').removeClass('hidden');
      $(this).children('.arrowUp').addClass('show');


    } else {

      $(this).children('.arrowUp').addClass('hidden');
      $(this).children('.arrowUp').removeClass('show');


    }


  });


  // Tooltips
  $('.js-tooltip').tooltip();

  // Copy to clipboard
  // Grab any text in the attribute 'data-copy' and pass it to the 
  // copy function
  $('.js-copy').click(function () {
    let text = $(this).attr('data-copy');
    let el = $(this);
    copyToClipboard(text, el);
  });
});
